mapper.select:
SELECT c.c_id AS id, c.c_name AS communityName, cn.cn_name AS natureName, cn.cn_id AS natureId, u.u_name AS createUserName, c.c_create_time AS createTime, p.p_name AS projectName, p.p_id AS projectId, (SELECT COUNT(b.b_id) FROM vi_building b WHERE b.b_community_id = c.c_id GROUP BY c.c_id DESC LIMIT 1) AS buildingCount FROM (( vi_community c LEFT JOIN vi_community_nature cn ON c.c_community_nature_id = cn.cn_id ) LEFT JOIN vi_user u ON c.c_create_user_id = u.u_id ) LEFT JOIN vi_project p ON c.c_project_id = p.p_id WHERE 1 = 1
AND c.c_name like CONCAT('%',#{communityName},'%')
AND c.c_project_id = #{projectId}

select
from vi_community where 1 = 1
and c_project_id = #{id}
