mapper.select:
SELECT d.d_id AS id, d.d_mac AS MAC, d.d_gw_mac AS GwMac, d.d_model AS Device, d.d_name AS NAME, d.d_feature AS Feature, d.d_active AS Active, d.d_index AS idx, d.d_create_user_id AS userId, d.d_create_time AS createTime, d.d_ele AS ele, p.p_name AS projectName, c.c_name AS communityName, b.b_name AS buildingName, unit.u_name AS unitName, f.f_name AS floorName, r.r_name AS roomName, u.u_name AS createUserName FROM vi_device d LEFT JOIN vi_user u ON d.d_create_user_id = u.u_id LEFT JOIN vi_room r ON d.d_room_id = r.r_id LEFT JOIN vi_floor f ON r.r_floor_id = f.f_id LEFT JOIN vi_unit unit ON f.f_unit_id = unit.u_id LEFT JOIN vi_building b ON unit.u_building_id = b.b_id LEFT JOIN vi_community c ON b.b_community_id = c.c_id LEFT JOIN vi_project p ON d.d_project_id = p.p_id WHERE 1 = 1
AND p.p_id = #{projectId}
AND c.c_id = #{communityId}
AND b.b_id = #{buildingId}
AND unit.u_id = #{unitId}
AND f.f_id = #{floorId}
AND r.r_id = #{roomId}
AND d.d_model = #{deviceModel}
AND d.d_name like CONCAT('%',#{deviceName},'%')
AND d.d_feature = #{deviceFeature}
AND d.d_mac = #{deviceMac}
AND d.d_gw_mac = #{gwMac}

mapper.update:
update vi_device
set
    d_ele = #{battery},
    d_update_time = #{updateTime}
where d_mac = #{mac}
