mapper.select:
SELECT r.r_id AS id, r.r_name AS roomName, rn.rn_name AS natureName, rn.rn_id AS natureId, rm.rm_name AS roomModelName, rm.rm_id AS roomModelId, r.r_area AS area, r.r_state AS state, f.f_name AS floorName, f.f_id AS floorId, ( SELECT COUNT( rd.rd_id ) FROM vi_room_device rd WHERE rd.rd_room_id = r.r_id ORDER BY r.r_id DESC LIMIT 1 ) AS deviceCount, ( SELECT COUNT( ru.ru_id ) FROM vi_room_user ru WHERE ru.ru_room_id = r.r_id ORDER BY r.r_id DESC LIMIT 1 ) AS userCount, r.r_create_time AS createTime, u.u_name AS createUserName FROM vi_room r INNER JOIN vi_room_nature rn ON r.r_room_nature_id = rn.rn_id INNER JOIN vi_room_model rm ON r.r_room_model_id = rm.rm_id INNER JOIN vi_user u ON r.r_create_user_id = u.u_id INNER JOIN vi_floor f ON r.r_floor_id = f.f_id WHERE 1 = 1
AND r.r_name like CONCAT('%',#{roomName},'%')
AND r.r_floor_id = #{floorId}
