mapper.select:
SELECT CONCAT(p.p_name, c.c_name, b.b_name, unit.u_name, f.f_name, r.r_name) AS roomFullName, r.r_id AS id, r.r_name AS roomName, rn.rn_name AS natureName, rn.rn_id AS natureId, rm.rm_name AS roomModelName, rm.rm_id AS roomModelId, r.r_area AS area, r.r_state AS state, f.f_name AS floorName, f.f_id AS floorId, ( SELECT COUNT( d.d_id ) FROM vi_device d WHERE d.d_room_id = r.r_id ORDER BY r.r_id DESC LIMIT 1 ) AS deviceCount, ( SELECT COUNT( ru.ru_id ) FROM vi_room_user ru WHERE ru.ru_room_id = r.r_id ORDER BY r.r_id DESC LIMIT 1 ) AS userCount, r.r_create_time AS createTime, u.u_name AS createUserName, d.d_name AS defaultDevice FROM vi_room r LEFT JOIN vi_room_nature rn ON r.r_room_nature_id = rn.rn_id LEFT JOIN vi_room_model rm ON r.r_room_model_id = rm.rm_id LEFT JOIN vi_user u ON r.r_create_user_id = u.u_id LEFT JOIN vi_floor f ON r.r_floor_id = f.f_id LEFT JOIN vi_unit unit ON f.f_unit_id = unit.u_id LEFT JOIN vi_building b ON unit.u_building_id = b.b_id LEFT JOIN vi_community c ON b.b_community_id = c.c_id LEFT JOIN vi_project p ON c.c_project_id = p.p_id LEFT JOIN (SELECT * FROM vi_device WHERE d_is_default = 1) d ON d.d_room_id = r.r_id WHERE 1 = 1
AND r.r_name like CONCAT('%',#{roomName},'%')
AND r.r_floor_id = #{floorId}
AND c.c_project_id = #{projectId}

select
from vi_room where 1 = 1
and r_floor_id = #{id}

select count(r_id)
from vi_room
where r_floor_id = #{id}
